mapper.select:
select

from tb_blog_comment
where is_deleted=0

  AND blog_id = #{blogId}


  AND comment_status = #{commentStatus}

order by comment_id desc

  limit #{start},#{limit}

select count(*) from tb_blog_comment
where is_deleted=0

  AND blog_id = #{blogId}


  AND comment_status = #{commentStatus}

mapper.update:
update tb_blog_comment set comment_status=1 where comment_id in
#{id}
and comment_status = 0

update tb_blog_comment
set is_deleted=1 where comment_id in

  #{id}
